mapper.select:
select ai.id, ai.staff_id, ai.task_title, ai.content, ai.status, ai.type, ai.create_date, ai.complate_date, si.code as staffCode, si.name as staffName, si.images as staffImages, ei.name as enterpriseName, ei.images as enterpriseImages, di.dept_name, poi.name as positionName from agent_info ai left join staff_info si on (si.id = ai.staff_id) left join enterprise_info ei on (ei.id = si.enterprise_id) left join dept_info di on (di.id = si.dept_id) left join position_info poi on (poi.id = si.position_id) where 1 = 1
AND ai.task_title LIKE CONCAT('%',#{agentInfo.taskTitle},'%')
AND si.name LIKE CONCAT('%',#{agentInfo.staffName},'%')
AND si.user_id = #{agentInfo.staffId}
AND ei.user_id = #{agentInfo.enterpriseId}
order by ai.create_date desc

SELECT DATE_FORMAT(spo.days, '%m-%d') AS days, IFNULL(COUNT(oi.id), 0) AS count FROM ( SELECT DATE_SUB( curdate(), INTERVAL + 0 DAY ) days UNION SELECT DATE_SUB( curdate(), INTERVAL + 1 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 2 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 3 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 4 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 5 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 6 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 7 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 8 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 9 DAY )) spo LEFT JOIN agent_info oi ON ( DATE_FORMAT( oi.create_date, '%Y-%m-%d' ) = DATE_FORMAT( spo.days, '%Y-%m-%d' )
AND oi.enterprise_id = #{enterpriseId}
) WHERE 1 = 1 GROUP BY days ORDER BY days ASC

SELECT DATE_FORMAT(spo.days, '%m-%d') AS days, IFNULL(COUNT(oi.id), 0) AS count FROM ( SELECT DATE_SUB( curdate(), INTERVAL + 0 DAY ) days UNION SELECT DATE_SUB( curdate(), INTERVAL + 1 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 2 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 3 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 4 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 5 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 6 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 7 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 8 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 9 DAY )) spo LEFT JOIN agent_info oi ON ( DATE_FORMAT( oi.create_date, '%Y-%m-%d' ) = DATE_FORMAT( spo.days, '%Y-%m-%d' ) AND oi.status != 0
AND oi.enterprise_id = #{enterpriseId}
) WHERE 1 = 1 GROUP BY days ORDER BY days ASC
